const withPWA = require('next-pwa')({
  dest: 'public',
  register: true,
  skipWaiting: true,
  disable: process.env.NODE_ENV === 'development',
});

/** @type {import('next').NextConfig} */
const nextConfig = {
  reactStrictMode: true,
  images: {
    remotePatterns: [
      {
        protocol: 'https',
        hostname: 'ncueapdxkkvhbdrqxguy.supabase.co',
      },
    ],
  },
};

module.exports = withPWA(nextConfig);

'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import Link from 'next/link';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import * as z from 'zod';
import { supabase } from '@/lib/supabase/client';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';

const loginSchema = z.object({
  email: z.string().email(),
  password: z.string().min(6),
});

type LoginForm = z.infer<typeof loginSchema>;

export default function LoginPage() {
  const router = useRouter();
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);

  const { register, handleSubmit, formState: { errors } } = useForm<LoginForm>({
    resolver: zodResolver(loginSchema),
  });

  const onSubmit = async (data: LoginForm) => {
    setLoading(true);
    setError(null);
    try {
      const { error } = await supabase.auth.signInWithPassword({
        email: data.email,
        password: data.password,
      });

      if (error) throw error;
      router.push('/dashboard');
      router.refresh();
    } catch (err: any) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="space-y-6">
      <div className="text-center">
        <h2 className="text-3xl font-bold tracking-tight text-slate-900 dark:text-slate-100">
          Welcome back
        </h2>
        <p className="mt-2 text-sm text-slate-600 dark:text-slate-400">
          Sign in to your LifeOS account
        </p>
      </div>

      <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
        <div className="space-y-2">
          <Label htmlFor="email">Email</Label>
          <Input id="email" type="email" placeholder="you@example.com" {...register('email')} />
          {errors.email && <p className="text-sm text-red-500">{errors.email.message}</p>}
        </div>

        <div className="space-y-2">
          <Label htmlFor="password">Password</Label>
          <Input id="password" type="password" {...register('password')} />
          {errors.password && <p className="text-sm text-red-500">{errors.password.message}</p>}
        </div>

        {error && <div className="text-sm text-red-500 text-center">{error}</div>}

        <Button type="submit" className="w-full" disabled={loading}>
          {loading ? 'Signing in...' : 'Sign in'}
        </Button>
      </form>

      <div className="text-center text-sm">
        <span className="text-slate-600 dark:text-slate-400">Don't have an account? </span>
        <Link href="/signup" className="font-semibold text-primary hover:text-primary/80">
          Sign up
        </Link>
      </div>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import Link from 'next/link';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import * as z from 'zod';
import { supabase } from '@/lib/supabase/client';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';

const signupSchema = z.object({
  fullName: z.string().min(2, "Name must be at least 2 characters"),
  email: z.string().email(),
  password: z.string().min(6, "Password must be at least 6 characters"),
});

type SignupForm = z.infer<typeof signupSchema>;

export default function SignupPage() {
  const router = useRouter();
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);

  const { register, handleSubmit, formState: { errors } } = useForm<SignupForm>({
    resolver: zodResolver(signupSchema),
  });

  const onSubmit = async (data: SignupForm) => {
    setLoading(true);
    setError(null);
    try {
      const { data: authData, error: authError } = await supabase.auth.signUp({
        email: data.email,
        password: data.password,
        options: {
          data: {
            full_name: data.fullName,
          },
        },
      });

      if (authError) throw authError;

      if (authData.user) {
        // Manually create profile if trigger doesn't exist (failsafe)
        const { error: profileError } = await supabase
          .from('profiles')
          .insert({
            id: authData.user.id,
            full_name: data.fullName,
            username: data.email.split('@')[0],
          });

        if (profileError) {
          // Ignore duplicate key error if trigger ran
          if (profileError.code !== '23505') console.error("Profile creation error", profileError);
        }
      }

      router.push('/dashboard');
      router.refresh();
    } catch (err: any) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="space-y-6">
      <div className="text-center">
        <h2 className="text-3xl font-bold tracking-tight text-slate-900 dark:text-slate-100">
          Create an account
        </h2>
        <p className="mt-2 text-sm text-slate-600 dark:text-slate-400">
          Start your journey with LifeOS
        </p>
      </div>

      <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
        <div className="space-y-2">
          <Label htmlFor="fullName">Full Name</Label>
          <Input id="fullName" type="text" placeholder="John Doe" {...register('fullName')} />
          {errors.fullName && <p className="text-sm text-red-500">{errors.fullName.message}</p>}
        </div>

        <div className="space-y-2">
          <Label htmlFor="email">Email</Label>
          <Input id="email" type="email" placeholder="you@example.com" {...register('email')} />
          {errors.email && <p className="text-sm text-red-500">{errors.email.message}</p>}
        </div>

        <div className="space-y-2">
          <Label htmlFor="password">Password</Label>
          <Input id="password" type="password" {...register('password')} />
          {errors.password && <p className="text-sm text-red-500">{errors.password.message}</p>}
        </div>

        {error && <div className="text-sm text-red-500 text-center">{error}</div>}

        <Button type="submit" className="w-full" disabled={loading}>
          {loading ? 'Creating account...' : 'Sign up'}
        </Button>
      </form>

      <div className="text-center text-sm">
        <span className="text-slate-600 dark:text-slate-400">Already have an account? </span>
        <Link href="/login" className="font-semibold text-primary hover:text-primary/80">
          Sign in
        </Link>
      </div>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { supabase } from '@/lib/supabase/client';
import { DashboardHeader } from '@/components/dashboard/DashboardHeader';
import { DailySummaryCard } from '@/components/dashboard/DailySummaryCard';
import { GoalsOverview } from '@/components/dashboard/GoalsOverview';
import { QuickEntryWidget } from '@/components/dashboard/QuickEntryWidget';
import { CalendarHeatmap } from '@/components/dashboard/CalendarHeatmap';
import { useUser } from '@/lib/hooks/useUser';

export default function DashboardPage() {
  const { user } = useUser();
  const [goals, setGoals] = useState([]);
  const [todayEntries, setTodayEntries] = useState([]);
  const [loading, setLoading] = useState(true);

  // Simplified data loading without Opik for now to ensure base functionality first
  useEffect(() => {
    if (!user) return;

    loadDashboardData();
  }, [user]);

  async function loadDashboardData() {
    try {
      const { data: goalsData } = await supabase
        .from('goals')
        .select('*')
        .eq('user_id', user!.id) // user is guaranteed if this runs
        .eq('status', 'active')
        .limit(5);

      const { data: entriesData } = await supabase
        .from('entries')
        .select('*')
        .eq('user_id', user!.id)
        .gte('entry_date', new Date().toISOString().split('T')[0])
        .order('entry_date', { ascending: false });

      setGoals(goalsData || []);
      setTodayEntries(entriesData || []);
    } catch (error) {
      console.error('Failed to load dashboard:', error);
    } finally {
      setLoading(false);
    }
  }

  if (loading) {
    return <div className="p-6">Loading dashboard...</div>; // Could be a skeleton
  }

  return (
    <div className="max-w-7xl mx-auto p-4 md:p-6 space-y-6">
      <DashboardHeader userName={user?.user_metadata?.full_name || user?.email} />

      {/* Bento Layout */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2">
          <QuickEntryWidget onEntryCreated={loadDashboardData} />
          <div className="h-6"></div> {/* Spacer */}
          <DailySummaryCard entries={todayEntries} />
        </div>

        <div className="space-y-6 flex flex-col">
          <GoalsOverview goals={goals} />
          <CalendarHeatmap userId={user?.id || ''} />
        </div>
      </div>
    </div>
  );
}

