'use client';

import { useEffect, useState } from 'react';
import { supabase } from '@/lib/supabase/client';
import { Button } from '@/components/ui/button';
import { Plus, Search } from 'lucide-react';
import Link from 'next/link';
import { useUser } from '@/lib/hooks/useUser';
import { Input } from '@/components/ui/input';

export default function JournalPage() {
    const { user } = useUser();
    const [entries, setEntries] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [search, setSearch] = useState('');

    useEffect(() => {
        if (!user) return;
        loadEntries();
    }, [user]);

    async function loadEntries() {
        const { data } = await supabase
            .from('entries')
            .select('*')
            .eq('user_id', user!.id)
            .order('entry_date', { ascending: false });

        setEntries(data || []);
        setLoading(false);
    }

    const filteredEntries = entries.filter(e =>
        e.content.toLowerCase().includes(search.toLowerCase()) ||
        e.tags?.some((t: string) => t.toLowerCase().includes(search.toLowerCase()))
    );

    return (
        <div className="max-w-4xl mx-auto p-4 md:p-6 space-y-6">
            <div className="flex justify-between items-center">
                <h1 className="text-3xl font-bold">Journal</h1>
                <Link href="/journal/new">
                    <Button className="rounded-full">
                        <Plus className="w-5 h-5 mr-2" /> New Entry
                    </Button>
                </Link>
            </div>

            <div className="relative">
                <Search className="absolute left-3 top-3 h-5 w-5 text-slate-400" />
                <Input
                    className="pl-10"
                    placeholder="Search entries..."
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                />
            </div>

            <div className="space-y-4">
                {loading ? (
                    <div>Loading entries...</div>
                ) : filteredEntries.length === 0 ? (
                    <div className="text-center py-12 text-slate-500">
                        No entries found.
                    </div>
                ) : (
                    filteredEntries.map((entry) => (
                        <div key={entry.id} className="bg-white dark:bg-slate-800/50 p-6 rounded-[24px] shadow-sm hover:shadow-md transition-shadow">
                            <div className="flex justify-between items-start mb-4">
                                <div className="flex items-center gap-3">
                                    <div className="h-10 w-10 bg-slate-100 dark:bg-slate-700 rounded-xl flex items-center justify-center text-xl">
                                        ðŸ“„
                                    </div>
                                    <div>
                                        <p className="font-semibold text-sm text-slate-500">{new Date(entry.entry_date).toLocaleDateString(undefined, { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</p>
                                    </div>
                                </div>
                                {entry.mood && (
                                    <span className="px-3 py-1 rounded-full bg-primary/10 text-primary text-xs font-bold uppercase">
                                        {entry.mood}
                                    </span>
                                )}
                            </div>

                            <p className="text-slate-800 dark:text-slate-200 leading-relaxed whitespace-pre-wrap">
                                {entry.content}
                            </p>

                            {(entry.tags?.length > 0 || entry.ai_summary) && (
                                <div className="mt-4 pt-4 border-t border-slate-100 dark:border-slate-800 space-y-3">
                                    {entry.ai_summary && (
                                        <div className="bg-slate-50 dark:bg-slate-900/50 p-3 rounded-lg text-sm text-slate-600 dark:text-slate-400 italic">
                                            âœ¨ AI Summary: {entry.ai_summary}
                                        </div>
                                    )}
                                    {entry.tags?.length > 0 && (
                                        <div className="flex flex-wrap gap-2">
                                            {entry.tags.map((tag: string) => (
                                                <span key={tag} className="text-xs text-slate-500 bg-slate-100 dark:bg-slate-800 px-2 py-1 rounded-md">
                                                    #{tag}
                                                </span>
                                            ))}
                                        </div>
                                    )}
                                </div>
                            )}
                        </div>
                    ))
                )}
            </div>
        </div>
    );
}

'use client'

import { useState, useEffect } from 'react'
import { createClient } from '@/lib/supabase/client'
import { useUserStore } from '@/store/userStore'
import { getWeeklyReflectionPrompt } from '@/lib/ai/prompts'
import { chatWithClaude } from '@/lib/ai/claude'

type Reflection = {
  id: string
  period_type: 'week' | 'month'
  start_date: string
  end_date: string
  summary: string
  insights: any
  metrics: any
  created_at: string
}

export default function ReflectionsPage() {
  const [reflections, setReflections] = useState<Reflection[]>([])
  const [currentReflection, setCurrentReflection] = useState<Reflection | null>(null)
  const [isGenerating, setIsGenerating] = useState(false)
  const [loading, setLoading] = useState(true)

  const { user } = useUserStore()
  const supabase = createClient()

  useEffect(() => {
    if (user) {
      loadReflections()
    }
  }, [user])

  const loadReflections = async () => {
    if (!user) return

    setLoading(true)
    const { data } = await supabase
      .from('reflections')
      .select('*')
      .eq('user_id', user.id)
      .order('created_at', { ascending: false })

    setReflections((data || []).map(reflection => ({
      ...reflection,
      period_type: reflection.period_type as 'week' | 'month'
    })))
    setLoading(false)
  }

  const generateWeeklyReflection = async () => {
    if (!user) return

    setIsGenerating(true)

    try {
      // Get this week's data
      const weekAgo = new Date()
      weekAgo.setDate(weekAgo.getDate() - 7)

      const [entriesResult, goalsResult] = await Promise.all([
        supabase.from('entries')
          .select('*')
          .eq('user_id', user.id)
          .gte('entry_date', weekAgo.toISOString()),
        supabase.from('goals')
          .select('*, categories(name)')
          .eq('user_id', user.id)
      ])

      const weeklyData = {
        entries: entriesResult.data || [],
        goals: goalsResult.data || [],
        moodTrends: calculateMoodTrends(entriesResult.data || []),
        topTags: calculateTopTags(entriesResult.data || [])
      }

      const prompt = getWeeklyReflectionPrompt(weeklyData)
      const response = await chatWithClaude([{ role: 'user', content: prompt }],
        'You are a compassionate reflection guide helping users understand their weekly journey.')

      // Save reflection
      const { data: reflection } = await supabase
        .from('reflections')
        .insert({
          user_id: user.id,
          period_type: 'week',
          start_date: weekAgo.toISOString().split('T')[0],
          end_date: new Date().toISOString().split('T')[0],
          summary: response.content,
          insights: weeklyData,
          metrics: {
            entriesCount: weeklyData.entries.length,
            goalsActive: weeklyData.goals.filter(g => g.status === 'active').length,
            avgMood: calculateAverageMood(entriesResult.data || [])
          }
        })
        .select()
        .single()

      if (reflection) {
        setCurrentReflection({
          ...reflection,
          period_type: reflection.period_type as 'week' | 'month'
        } as Reflection)
      }
      loadReflections() // Refresh list

    } catch (error) {
      console.error('Error generating reflection:', error)
      // TODO: Show error notification
    } finally {
      setIsGenerating(false)
    }
  }

  const calculateMoodTrends = (entries: any[]) => {
    const moodOrder = ['terrible', 'bad', 'okay', 'good', 'great']
    return entries
      .sort((a, b) => new Date(a.entry_date).getTime() - new Date(b.entry_date).getTime())
      .slice(-7) // Last 7 entries
      .map(entry => entry.mood || 'okay')
  }

  const calculateTopTags = (entries: any[]) => {
    const tagCount: Record<string, number> = {}

    entries.forEach(entry => {
      if (entry.tags) {
        entry.tags.forEach((tag: string) => {
          tagCount[tag] = (tagCount[tag] || 0) + 1
        })
      }
    })

    return Object.entries(tagCount)
      .sort(([,a], [,b]) => b - a)
      .slice(0, 5)
      .map(([tag]) => tag)
  }

  const calculateAverageMood = (entries: any[]) => {
    if (entries.length === 0) return 'N/A'

    const moodScores = { terrible: 1, bad: 2, okay: 3, good: 4, great: 5 }
    const totalScore = entries.reduce((sum, entry) => {
      return sum + (moodScores[entry.mood as keyof typeof moodScores] || 3)
    }, 0)

    const avgScore = totalScore / entries.length
    const moodLabels = ['terrible', 'bad', 'okay', 'good', 'great']
    return moodLabels[Math.round(avgScore) - 1] || 'okay'
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-background-light dark:bg-background-dark flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
