        </div>

        {/* Current Week Reflection */}
        {currentReflection && (
          <div className="bg-white dark:bg-slate-800 rounded-xl p-8 mb-8 shadow-sm border border-slate-200 dark:border-slate-700">
            <div className="flex items-center justify-between mb-6">
