import { NextRequest, NextResponse } from 'next/server';
import { createServerClient } from '@supabase/ssr';
import { cookies } from 'next/headers';
import { streamGemini } from '@/lib/ai/gemini';
import { opik } from '@/lib/opik/client';
import { COMPANION_SYSTEM_INSTRUCTION, CHAT_CONTEXT_BUILDER } from '@/lib/ai/prompts';

export async function POST(req: NextRequest) {
    try {
        // Auth check
        const cookieStore = await cookies();
        const supabase = createServerClient(
            process.env.NEXT_PUBLIC_SUPABASE_URL!,
            process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
            {
                cookies: {
                    getAll() { return cookieStore.getAll() },
                    setAll(cookiesToSet) { }
                },
            }
        );
        const { data: { user } } = await supabase.auth.getUser();

        if (!user) {
            return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
        }

        const { messages, conversationId } = await req.json();

        // Get user context for personalization
        const userData = await getUserContext(supabase, user.id);
        const systemInstruction = COMPANION_SYSTEM_INSTRUCTION + '\n\n' + CHAT_CONTEXT_BUILDER(userData);

        // Stream response
        const encoder = new TextEncoder();
        const stream = new ReadableStream({
            async start(controller) {
                await streamGemini(
                    messages,
                    systemInstruction,
                    (chunk) => {
                        controller.enqueue(encoder.encode(`data: ${JSON.stringify({ chunk })}\n\n`));
                    }
                );
                controller.close();
            },
        });

        // Log to Opik (non-blocking)
        opik.trace({
            name: 'ai-chat',
            input: { messages, conversationId },
            metadata: { userId: user.id },
        }).catch(console.error);

        return new NextResponse(stream, {
            headers: {
                'Content-Type': 'text/event-stream',
                'Cache-Control': 'no-cache',
                'Connection': 'keep-alive',
            },
        });
    } catch (error) {
        console.error('Chat error:', error);
        return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
    }
}

async function getUserContext(supabase: any, userId: string) {
    // Fetch recent entries, active goals
    // Wrap in try/catch to avoid breaking chat if DB query fails
    try {
        const [entries, goals] = await Promise.all([
            supabase
                .from('entries')
                .select('entry_date, content')
                .eq('user_id', userId)
                .order('entry_date', { ascending: false })
                .limit(5),
            supabase
                .from('goals')
                .select('title, frequency, current_value, target_value, streak')
                .eq('user_id', userId)
                .eq('status', 'active'),
        ]);

        return {
            recentEntries: entries.data || [],
            activeGoals: goals.data || [],
            weeklyStats: {
                totalEntries: entries.data?.length || 0,
                goalsHit: 0,
                totalGoals: goals.data?.length || 0,
                topCategories: [],
            },
        };
    } catch (e) {
        console.error("Context fetch failed", e);
        return { recentEntries: [], activeGoals: [], weeklyStats: { totalEntries: 0, goalsHit: 0, totalGoals: 0, topCategories: [] } };
    }
}

import { NextRequest, NextResponse } from 'next/server';
import { geminiJSON } from '@/lib/ai/gemini';
import { SUMMARIZE_ENTRY_INSTRUCTION } from '@/lib/ai/prompts';
import { opik } from '@/lib/opik/client';

export async function POST(req: NextRequest) {
    try {
        const { content } = await req.json();

        if (!content) {
            return NextResponse.json({ error: 'Content required' }, { status: 400 });
        }

        const prompt = `Analyze this journal entry:\n\n"${content}"`;

        // Use Gemini JSON mode
        const result = await geminiJSON<{
            summary: string;
            tags: string[];
            mood: string;
        }>(prompt, SUMMARIZE_ENTRY_INSTRUCTION);

        // Log to Opik (fire and forget)
        opik.trace({
            name: 'entry-summarization',
            input: { content },
            output: result,
            metadata: { model: 'gemini-1.5-pro' },
        }).catch((err: any) => console.error("Opik trace error", err));

        return NextResponse.json(result);
    } catch (error) {
        console.error('Summarization error:', error);
        return NextResponse.json({ error: 'Failed to summarize' }, { status: 500 });
    }
}

