'use client';

import { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { VoiceInput } from './VoiceInput';
import { TagSelector } from './TagSelector';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Loader2 } from 'lucide-react';
import { cn } from '@/lib/utils';

const entrySchema = z.object({
  content: z.string().min(5, 'Entry must be at least 5 characters'),
  mood: z.enum(['great', 'good', 'okay', 'bad', 'terrible']).optional(),
  tags: z.array(z.string()).optional(),
});

type EntryFormData = z.infer<typeof entrySchema>;

export function EntryEditor({ onSave, initialContent = '' }: { onSave: (data: any) => Promise<void>, initialContent?: string }) {
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [autoSuggestions, setAutoSuggestions] = useState<any>(null);

  const { register, handleSubmit, watch, setValue, formState: { errors } } = useForm<EntryFormData>({
    resolver: zodResolver(entrySchema),
    defaultValues: {
      content: initialContent,
      tags: [],
    }
  });

  const content = watch('content');

  // Auto-analyze content as user types (debounced)
  useEffect(() => {
    if (!content || content.length < 50) return;

    const timer = setTimeout(async () => {
      try {
        setIsAnalyzing(true);
        // Call our internal API instead of direct geminiJSON to keep secrets safe
        const response = await fetch('/api/ai/summarize', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ content }),
        });

        if (!response.ok) throw new Error('Analysis failed');

        const suggestions = await response.json();
        setAutoSuggestions(suggestions);

        // Auto-fill suggestions but dont overwrite if user manually selected (simplified logic)
        if (suggestions.tags && (!watch('tags') || watch('tags')?.length === 0)) {
          setValue('tags', suggestions.tags);
        }
        if (suggestions.mood && !watch('mood')) {
          setValue('mood', suggestions.mood as any);
        }
      } catch (error) {
        console.error('Analysis failed:', error);
      } finally {
        setIsAnalyzing(false);
      }
    }, 2000);

    return () => clearTimeout(timer);
  }, [content, setValue, watch]);

  const onSubmit = async (data: EntryFormData) => {
    await onSave({
      ...data,
      ai_summary: autoSuggestions?.summary,
    });
  };

  const handleVoiceInput = (text: string) => {
    const current = content || '';
    setValue('content', current + (current ? ' ' : '') + text);
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-xl font-semibold">New Entry</h2>
        <VoiceInput onTranscript={handleVoiceInput} />
      </div>

      <Textarea
        {...register('content')}
        placeholder="What happened today?"
        className="min-h-[300px] text-lg leading-relaxed resize-none p-4"
        autoFocus
      />

      {errors.content && (
        <p className="text-sm text-red-500">{errors.content.message}</p>
      )}

      {isAnalyzing && (
        <div className="flex items-center text-sm text-primary animate-pulse">
          <Loader2 className="animate-spin mr-2 h-4 w-4" />
          Analyzing your entry with AI...
        </div>
      )}

      <div className="space-y-4">
        <div>
          <label className="text-sm font-medium mb-2 block">Mood</label>
          <div className="flex flex-wrap gap-2">
            {['great', 'good', 'okay', 'bad', 'terrible'].map((mood) => (
              <Button
                key={mood}
                type="button"
                variant={watch('mood') === mood ? 'default' : 'outline'}
                onClick={() => setValue('mood', mood as any)}
                className="capitalize"
              >
                {mood}
              </Button>
            ))}
          </div>
        </div>

        <div>
          <label className="text-sm font-medium mb-2 block">Tags</label>
          <TagSelector
            selectedTags={watch('tags') || []}
            onTagsChange={(tags) => setValue('tags', tags)}
            suggestions={autoSuggestions?.tags || []}
          />
        </div>
      </div>

      <Button type="submit" size="lg" className="w-full h-12 text-lg">
        Save Entry
      </Button>
    </form>
  );
}

'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { X, Plus } from 'lucide-react';
import { Input } from '@/components/ui/input';

interface TagSelectorProps {
  selectedTags: string[];
  onTagsChange: (tags: string[]) => void;
  suggestions?: string[];
}

export function TagSelector({ selectedTags, onTagsChange, suggestions = [] }: TagSelectorProps) {
  const [inputVisible, setInputVisible] = useState(false);
  const [inputValue, setInputValue] = useState('');

  const toggleTag = (tag: string) => {
    if (selectedTags.includes(tag)) {
      onTagsChange(selectedTags.filter(t => t !== tag));
    } else {
      onTagsChange([...selectedTags, tag]);
    }
  };

  const addCustomTag = () => {
    if (inputValue.trim() && !selectedTags.includes(inputValue.trim())) {
      onTagsChange([...selectedTags, inputValue.trim()]);
    }
    setInputValue('');
    setInputVisible(false);
  };

  return (
    <div className="space-y-3">
      <div className="flex flex-wrap gap-2">
        {selectedTags.map(tag => (
          <span
            key={tag}
            className="inline-flex items-center gap-1 px-3 py-1 rounded-full bg-primary/10 text-primary text-sm font-medium"
          >
            {tag}
            <button type="button" onClick={() => toggleTag(tag)} className="hover:text-primary/70">
              <X className="w-3 h-3" />
            </button>
          </span>
        ))}

        {inputVisible ? (
          <div className="flex items-center gap-2">
            <Input
              autoFocus
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addCustomTag())}
              onBlur={() => inputValue ? addCustomTag() : setInputVisible(false)}
              className="h-7 w-32 text-xs"
              placeholder="New tag..."
            />
          </div>
        ) : (
          <button
            type="button"
            onClick={() => setInputVisible(true)}
            className="inline-flex items-center gap-1 px-3 py-1 rounded-full border border-dashed border-slate-300 text-slate-500 text-sm hover:border-primary hover:text-primary transition-colors"
          >
            <Plus className="w-3 h-3" /> Add Tag
          </button>
        )}
      </div>

      {suggestions.length > 0 && (
        <div className="flex flex-wrap gap-2 items-center text-xs text-slate-500">
          <span>Suggestions:</span>
          {suggestions.filter(s => !selectedTags.includes(s)).map(suggestion => (
            <button
              key={suggestion}
              type="button"
              onClick={() => toggleTag(suggestion)}
              className="px-2 py-1 rounded-md bg-slate-100 hover:bg-slate-200 transition-colors"
            >
              + {suggestion}
            </button>
          ))}
        </div>
      )}
    </div>
  );
}

