import { Opik } from 'opik';
import { chatWithGemini } from '@/lib/ai/gemini';

// Initialize Opik only if API key is present to avoid errors during build/dev without keys
// In a real app we might want strict checks
const opikClient = process.env.OPIK_API_KEY
    ? new Opik({
        apiKey: process.env.OPIK_API_KEY!,
        projectName: process.env.OPIK_PROJECT_NAME || 'lifeos',
    })
    : null; // Graceful fallback if not configured

export const opik = opikClient || {
    // Mock interface so code doesn't break if opik is missing
    trace: async () => { },
    span: async () => { },
    score: async () => { },
    log: async () => { },
} as any;

// Wrapper for Gemini calls with Opik logging
export async function geminiWithOpik(
    operationName: string,
    messages: any[],
    systemInstruction: string,
    metadata?: Record<string, any>
) {
    const startTime = Date.now();
    const traceId = crypto.randomUUID();

    // Start Opik trace
    await opik.trace({
        id: traceId,
        name: operationName,
        input: {
            messages,
            systemInstruction,
            model: 'gemini-1.5-pro'
        },
        metadata: {
            ...metadata,
            timestamp: new Date().toISOString(),
        },
    });

    try {
        const response = await chatWithGemini(messages, systemInstruction);
        const duration = Date.now() - startTime;

        // Log successful completion
        await opik.span({
            traceId,
            name: `\${operationName}-completion`,
            input: messages[messages.length - 1],
            output: response,
            metadata: {
                duration,
                success: true,
                tokensEstimate: response.length / 4,
            },
        });

        return response;
    } catch (error: any) {
        // Log error
        await opik.span({
            traceId,
            name: `\${operationName}-error`,
            metadata: {
                error: error.message,
                success: false,
            },
        });
        throw error;
    }
}

export async function trackFeatureUsage(
    featureName: string,
    userId: string,
    metadata?: Record<string, any>
) {
    await opik.log({
        name: 'feature_usage',
        properties: {
            feature: featureName,
            userId,
            timestamp: new Date().toISOString(),
            ...metadata,
        },
    });
}

import { createServerClient } from '@supabase/ssr'
import { NextResponse, type NextRequest } from 'next/server'
import type { Database } from '@/types/database.types'

export async function updateSession(request: NextRequest) {
  let supabaseResponse = NextResponse.next({
    request,
  })

  const supabase = createServerClient<Database>(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
    {
      cookies: {
        getAll() {
          return request.cookies.getAll()
        },
        setAll(cookiesToSet) {
          cookiesToSet.forEach(({ name, value, options }) => request.cookies.set(name, value))
          supabaseResponse = NextResponse.next({
            request,
          })
          cookiesToSet.forEach(({ name, value, options }) =>
            supabaseResponse.cookies.set(name, value, options)
          )
        },
      },
    }
  )

  // IMPORTANT: Avoid writing any logic between createServerClient and
  // supabase.auth.getUser(). A simple mistake could make it very hard to debug
  // issues with users being randomly logged out.

  const {
    data: { user },
  } = await supabase.auth.getUser()

  // IMPORTANT: You *must* return the supabaseResponse object as it is. If you're
  // creating a new response object with NextResponse.next() make sure to:
  // 1. Pass the request in it, like so:
  //    const myNewResponse = NextResponse.next({ request })
  // 2. Copy over the cookies, like so:
  //    myNewResponse.cookies.setAll(supabaseResponse.cookies.getAll())
  // 3. Change the myNewResponse object instead of the supabaseResponse object
  //    before returning it.

  return supabaseResponse
}

import { create } from 'zustand'
import { persist } from 'zustand/middleware'

type Theme = 'light' | 'dark' | 'system'

interface ThemeState {
  theme: Theme
  setTheme: (theme: Theme) => void
  resolvedTheme: 'light' | 'dark'
  setResolvedTheme: (theme: 'light' | 'dark') => void
}

export const useThemeStore = create<ThemeState>()(
  persist(
    (set, get) => ({
      theme: 'system',
      resolvedTheme: 'light',
      setTheme: (theme) => {
        set({ theme })
        // Apply theme to document
        const root = window.document.documentElement
        root.classList.remove('light', 'dark')

        if (theme === 'system') {
          const systemTheme = window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light'
          root.classList.add(systemTheme)
          set({ resolvedTheme: systemTheme })
        } else {
          root.classList.add(theme)
          set({ resolvedTheme: theme })
        }
      },
      setResolvedTheme: (resolvedTheme) => set({ resolvedTheme }),
    }),
    {
      name: 'theme-storage',
      partialize: (state) => ({ theme: state.theme }),
    }
  )
)

// Initialize theme on client side
if (typeof window !== 'undefined') {
  const theme = useThemeStore.getState().theme
  useThemeStore.getState().setTheme(theme)
}

